import React, { useEffect, useState } from "react";
import "./Home.css"; // Ensure this CSS file exists and is linked correctly

// Import your images.
// IMPORTANT: For a slideshow, you typically want DIFFERENT images here.
// Make sure these files exist in the './images/' directory
// and that their filenames (including case and extension) match exactly.
import diwali1 from "./images/diwali1.jpg";
// import diwali2 from "./images/diwali2.jpg"; // Uncomment and import if you have more images
// import diwali3 from "./images/diwali3.jpg"; // Uncomment and import if you have more images

export default function Home() {
  const greetingText = "Welcome to Sri Pathrakali Crackers"; // Your updated greeting text

  // Use all your imported images for the slideshow
  // Make sure these are different image variables if you want a varied slideshow.
  // If you uncommented diwali2 and diwali3 imports, update this array:
  const images = [diwali1, diwali1, diwali1]; // Using diwali1 three times as per your commented imports

  const [currentSlide, setCurrentSlide] = useState(0);

  // Slideshow auto change every 2.5 seconds
  useEffect(() => {
    const slideTimer = setInterval(() => {
      setCurrentSlide((prev) => (prev + 1) % images.length);
    }, 2500); // Change slide every 2.5 seconds
    return () => clearInterval(slideTimer);
  }, [images.length]);

  return (
    <div className="home-container">
      {/* HEADER SECTION */}
      <header className="main-header">
        {/* Your Site Title/Logo */}
        <h1 className="site-title">Sri Pathrakali Crackers</h1>

        {/* Navigation Bar (Optional, can be omitted if no links) */}
        <nav className="main-nav">
          <ul>
            <li><a href="/">Home</a></li>
            <li><a href="/products">Products</a></li>
            <li><a href="/about">About Us</a></li>
            <li><a href="/contact">Contact</a></li>
          </ul>
        </nav>

        {/* Container for the continuous text animation - part of the header content */}
        <div className="greeting-wrapper">
          <div className="greeting-text-inner">
            <span>{greetingText}</span> {/* First instance of the text */}
            <span>{greetingText}</span> {/* Second instance for seamless looping */}
          </div>
        </div>
      </header>

      {/* MAIN CONTENT SECTION (Slideshow and other content) */}
      <main className="main-content">
        {/* Slideshow section */}
        <div className="slideshow">
          <div
            className="slides"
            // This transform moves the entire 'slides' container horizontally
            // based on the current slide index, creating the sliding effect.
            style={{ transform: `translateX(-${currentSlide * 100}%)` }}
          >
            {images.map((src, idx) => (
              <img key={idx} src={src} alt={`Slide ${idx + 1}`} className="slide" />
            ))}
          </div>
          <div className="dots">
            {images.map((_, idx) => (
              <span
                key={idx}
                className={`dot ${idx === currentSlide ? "active" : ""}`}
                onClick={() => setCurrentSlide(idx)}
                aria-label={`Go to slide ${idx + 1}`}
              />
            ))}
          </div>
        </div>

        {/* You can add more content here below the slideshow */}
        <section className="about-section">
          <h2>About Our Crackers</h2>
          <p>Discover a wide range of high-quality and safe crackers for all your celebrations. We prioritize safety and customer satisfaction.</p>
          {/* More content */}
        </section>

      </main>

      {/* FOOTER SECTION (Optional) */}
      <footer className="main-footer">
        <p>&copy; 2025 Sri Pathrakali Crackers. All rights reserved.</p>
      </footer>
    </div>
  );
}
