import React, { useEffect, useState } from "react";
import "./Home.css";

import diwali1 from "./images/diwali1.jpg";
// Add more slideshow images if needed

export default function Home() {
  const greetingText = "Welcome to Sri Pathrakali Crackers";
  const images = [diwali1, diwali1, diwali1]; // Replace with different slideshow images

  const [currentSlide, setCurrentSlide] = useState(0);

  useEffect(() => {
    const slideTimer = setInterval(() => {
      setCurrentSlide((prev) => (prev + 1) % images.length);
    }, 2500);
    return () => clearInterval(slideTimer);
  }, [images.length]);

  return (
    <div className="home-container">
      {/* üîî Animated Greeting */}
      <div className="greeting-wrapper">
        <div className="greeting-text-inner">
          <span>{greetingText}</span>
          <span>{greetingText}</span>
        </div>
      </div>

      {/* üéûÔ∏è Slideshow */}
      <main className="main-content">
        <div className="slideshow">
          <div
            className="slides"
            style={{ transform: `translateX(-${currentSlide * 100}%)` }}
          >
            {images.map((src, idx) => (
              <img key={idx} src={src} alt={`Slide ${idx + 1}`} className="slide" />
            ))}
          </div>
          <div className="dots">
            {images.map((_, idx) => (
              <span
                key={idx}
                className={`dot ${idx === currentSlide ? "active" : ""}`}
                onClick={() => setCurrentSlide(idx)}
                aria-label={`Go to slide ${idx + 1}`}
              />
            ))}
          </div>
        </div>

        {/* üß® Product Cards */}
        <section className="products-section">
          <h2>Our Products</h2>
          <div className="products-grid">
            {[...Array(20)].map((_, idx) => (
              <div className="product-card" key={idx}>
                <img
                  src={`./images/product${(idx % 5) + 1}.jpg`} // Reuse or replace with actual product images
                  alt={`Product ${idx + 1}`}
                  className="product-image"
                />
                <button
                  className="order-button"
                  onClick={() => (window.location.href = "/products")}
                >
                  Order Now
                </button>
              </div>
            ))}
          </div>
        </section>
      </main>

      {/* üë£ Footer */}
      <footer className="main-footer">
        <p>&copy; 2025 Sri Pathrakali Crackers. All rights reserved.</p>
      </footer>
    </div>
  );
}
