import React, { useState, useRef } from 'react';
import CartItem from './CartItem';
import './CartDrawer.css';
import jsPDF from 'jspdf';
import html2canvas from 'html2canvas';

function CartDrawer({
  isOpen,
  onClose,
  cartItems,
  onRemoveFromCart,
  onUpdateQuantity,
  onClearCart
}) {
  const [showForm, setShowForm] = useState(false);
  const [formData, setFormData] = useState({ name: '', phone: '', location: '' });
  const billRef = useRef(null);

  const handleInputChange = (e) => {
    setFormData((prev) => ({ ...prev, [e.target.name]: e.target.value }));
  };

  const handleFirstSubmit = () => {
    const total = cartItems.reduce((sum, item) => sum + item.price * item.quantity, 0);
    if (total < 3000) {
      alert('âš ï¸ Purchase minimum â‚¹3000 to proceed.');
      return;
    }
    if (cartItems.length === 0) {
      alert('âš ï¸ Your cart is empty.');
      return;
    }
    setShowForm(true);
  };

  const handleFormSubmit = async () => {
    const { name, phone, location } = formData;

    if (!name.trim() || !phone.trim() || !location.trim()) {
      alert('âš ï¸ Please fill in all the details before submitting.');
      return;
    }

    try {
      const canvas = await html2canvas(billRef.current);
      const imgData = canvas.toDataURL('image/png');
      const pdf = new jsPDF('p', 'mm', 'a4');

      const imgProps = pdf.getImageProperties(imgData);
      const pdfWidth = pdf.internal.pageSize.getWidth();
      const pdfHeight = (imgProps.height * pdfWidth) / imgProps.width;

      pdf.addImage(imgData, 'PNG', 0, 0, pdfWidth, pdfHeight);
      pdf.save('CrackerMart_Bill.pdf');

      alert("ðŸŽ‰ PDF generated! Check your downloads.");

      if (onClearCart) onClearCart();
      onClose();
      setShowForm(false);
      setFormData({ name: '', phone: '', location: '' });
    } catch (error) {
      console.error('PDF generation failed:', error);
      alert('Failed to generate PDF.');
    }
  };

  const total = cartItems.reduce((sum, i) => sum + i.price * i.quantity, 0);

  return (
    <div className={`cart-drawer ${isOpen ? 'open' : ''}`}>
      <div className="cart-drawer-header">
        <h2>Your Cart</h2>
        <button onClick={onClose} className="close-button">Ã—</button>
      </div>

      {cartItems.length === 0 ? (
        <p className="empty-cart">Your cart is empty</p>
      ) : (
        <>
          <div className="cart-items">
            {cartItems.map((item) => (
              <CartItem
                key={item._id}
                item={item}
                onRemoveFromCart={onRemoveFromCart}
                onUpdateQuantity={onUpdateQuantity}
                disabled={false}
              />
            ))}
          </div>

          <div className="cart-drawer-summary">
            <h3>Total: â‚¹{total.toFixed(2)}</h3>

            {!showForm && (
              <button className="purchase-button" onClick={handleFirstSubmit}>
                Submit
              </button>
            )}

            {showForm && (
              <>
                <div className="checkout-form">
                  <input
                    type="text"
                    name="name"
                    placeholder="Your Name"
                    value={formData.name}
                    onChange={handleInputChange}
                    required
                  />
                  <input
                    type="tel"
                    name="phone"
                    placeholder="Phone Number"
                    value={formData.phone}
                    onChange={handleInputChange}
                    required
                  />
                  <input
                    type="text"
                    name="location"
                    placeholder="Your Place"
                    value={formData.location}
                    onChange={handleInputChange}
                    required
                  />
                  <button className="submit-final" onClick={handleFormSubmit}>
                    ðŸ“¥ Download Bill as PDF
                  </button>
                </div>

                <div ref={billRef} style={{ padding: '20px', background: '#fff', marginTop: '20px' }}>
                  <h2 style={{ textAlign: 'center' }}>ðŸ§¨ CrackerMart Bill ðŸ§¨</h2>
                  <p><strong>Name:</strong> {formData.name}</p>
                  <p><strong>Phone:</strong> {formData.phone}</p>
                  <p><strong>Location:</strong> {formData.location}</p>
                  <p><strong>Date:</strong> {new Date().toLocaleDateString('en-IN')}</p>
                  <hr />
                  <h4>Items Ordered:</h4>
                  <ul>
                    {cartItems.map((item, i) => (
                      <li key={i}>
                        {i + 1}. {item.name} â€” Qty: {item.quantity} Ã— â‚¹{item.price} = â‚¹{item.quantity * item.price}
                      </li>
                    ))}
                  </ul>
                  <h3 style={{ marginTop: '10px' }}>Total: â‚¹{total.toFixed(2)}</h3>
                  <p style={{ fontStyle: 'italic', fontSize: '12px' }}>Thank you for shopping with CrackerMart!</p>
                </div>
              </>
            )}
          </div>
        </>
      )}
    </div>
  );
}

export default CartDrawer;
