// src/components/CartDrawer.jsx
import React, { useState } from 'react';
import CartItem from './CartItem';
import './CartDrawer.css';

function CartDrawer({
  isOpen,
  onClose,
  cartItems,
  onRemoveFromCart,
  onUpdateQuantity
}) {
  const [showForm, setShowForm] = useState(false);
  const [submitted, setSubmitted] = useState(false);
  const [formData, setFormData] = useState({
    name: '',
    phone: '',
    location: ''
  });

  const handleInputChange = (e) => {
    setFormData((prev) => ({ ...prev, [e.target.name]: e.target.value }));
  };

  const handleFormSubmit = () => {
    const total = cartItems.reduce(
      (sum, item) => sum + item.price * item.quantity,
      0
    );

    if (total < 3000) {
      alert('âš ï¸ Purchase minimum â‚¹3000 to proceed.');
      return;
    }

    const message = `ðŸ›’ *Order Details*:
ðŸ‘¤ Name: ${formData.name}
ðŸ“ž Phone: ${formData.phone}
ðŸ“ Location: ${formData.location}

ðŸ§¨ *Items*:
${cartItems
  .map(
    (item) =>
      `â€¢ ${item.name} x${item.quantity} - â‚¹${item.price * item.quantity}`
  )
  .join('\n')}

ðŸ’° *Total: â‚¹${total.toFixed(2)}*`;

    const encoded = encodeURIComponent(message);
    const whatsappNumber = '8148842731';
    const whatsappURL = `https://wa.me/${whatsappNumber}?text=${encoded}`;

    window.open(whatsappURL, '_blank');
    setSubmitted(true); // mark as submitted
  };

  const handleAddMore = () => {
    setSubmitted(false);
    setShowForm(false);
    onClose(); // Close the drawer and go back to products
  };

  const isDisabled = submitted;

  return (
    <div className={`cart-drawer ${isOpen ? 'open' : ''}`}>
      <div className="cart-drawer-header">
        <h2>Your Cart</h2>
        <button onClick={onClose} className="close-button">
          Ã—
        </button>
      </div>

      {cartItems.length === 0 ? (
        <p className="empty-cart">Your cart is empty</p>
      ) : (
        <>
          <div className="cart-items">
            {cartItems.map((item) => (
              <CartItem
                key={item._id}
                item={item}
                onRemoveFromCart={!isDisabled ? onRemoveFromCart : () => {}}
                onUpdateQuantity={!isDisabled ? onUpdateQuantity : () => {}}
                disabled={isDisabled}
              />
            ))}
          </div>

          <div className="cart-drawer-summary">
            <h3>
              Total: â‚¹
              {cartItems
                .reduce((sum, i) => sum + i.price * i.quantity, 0)
                .toFixed(2)}
            </h3>

            {!showForm && !submitted && (
              <button
                className="purchase-button"
                onClick={() => setShowForm(true)}
              >
                Submit
              </button>
            )}

            {showForm && !submitted && (
              <div className="checkout-form">
                <input
                  type="text"
                  name="name"
                  placeholder="Your Name"
                  value={formData.name}
                  onChange={handleInputChange}
                  required
                />
                <input
                  type="tel"
                  name="phone"
                  placeholder="Phone Number"
                  value={formData.phone}
                  onChange={handleInputChange}
                  required
                />
                <input
                  type="text"
                  name="location"
                  placeholder="Your Place"
                  value={formData.location}
                  onChange={handleInputChange}
                  required
                />
                <button className="submit-final" onClick={handleFormSubmit}>
                  Submit Order
                </button>
              </div>
            )}

            {submitted && (
              <button className="add-more-button" onClick={handleAddMore}>
                Add More
              </button>
            )}
          </div>
        </>
      )}
    </div>
  );
}

export default CartDrawer;
