// src/components/Navbar.jsx
import React from "react";
import { Link } from "react-router-dom";
import { ShoppingCart } from "lucide-react";
import { GoogleLogin } from "@react-oauth/google";
import { jwtDecode } from "jwt-decode";
import "./Navbar.css";

function Navbar({ cartItemCount, onCartClick }) {
  const user = JSON.parse(localStorage.getItem("user"));

  const handleSuccess = (credentialResponse) => {
    try {
      const userData = jwtDecode(credentialResponse.credential);

      // ✅ Store only the needed fields
      const cleanUser = {
        name: userData.name,
        email: userData.email,
        picture: userData.picture || null,
      };

      localStorage.setItem("user", JSON.stringify(cleanUser));
      window.location.reload();
    } catch (err) {
      console.error("JWT Decode Error:", err);
    }
  };

  const handleLogout = () => {
    localStorage.removeItem("user");
    window.location.reload();
  };

  // ✅ Helper: Get first letter from name/email
  const getInitial = (user) => {
    if (user?.name) return user.name.charAt(0).toUpperCase();
    if (user?.email) return user.email.charAt(0).toUpperCase();
    return "?";
  };

  return (
    <nav className="navbar">
      {/* Logo */}
      <Link to="/" className="logo">
        Sri Pathrakali Crackers
      </Link>

      {/* Navigation Links */}
      <div className="nav-links">
        <Link to="/products">Products</Link>
        <Link to="/about">About</Link>
        <Link to="/after-payment">After Payment</Link>

        {/* Cart */}
        <button className="cart-button" onClick={onCartClick}>
          <ShoppingCart size={22} />
          <span style={{ marginLeft: "4px" }}>{cartItemCount}</span>
        </button>

        {/* Auth Section */}
        <div className="auth-section">
          {user ? (
            // ✅ If logged in → show profile pic or fallback initial + logout
            <div style={{ display: "flex", alignItems: "center", gap: "8px" }}>
              {user.picture ? (
                <img
                  src={user.picture}
                  alt="profile"
                  style={{ width: "32px", height: "32px", borderRadius: "50%" }}
                />
              ) : (
                <div
                  style={{
                    width: "32px",
                    height: "32px",
                    borderRadius: "50%",
                    background: "#007bff",
                    color: "white",
                    display: "flex",
                    alignItems: "center",
                    justifyContent: "center",
                    fontWeight: "bold",
                  }}
                >
                  {getInitial(user)}
                </div>
              )}

              <button
                onClick={handleLogout}
                style={{
                  background: "transparent",
                  border: "none",
                  color: "red",
                  cursor: "pointer",
                }}
              >
                Logout
              </button>
            </div>
          ) : (
            // ❌ If not logged in → show Google Sign-In button
            <GoogleLogin
              onSuccess={handleSuccess}
              onError={() => alert("❌ Login Failed")}
              useOneTap
            />
          )}
        </div>
      </div>
    </nav>
  );
}

export default Navbar;
